mapper.select:
SELECT DISTINCT s.id AS id, s.NAME AS 'name', s.url AS url, s.description AS description, s.icon AS icon, s.pid AS pid, s.seq AS seq, s.STATUS AS 'status', s.open_mode AS openMode, s.opened as opened, s.resource_type AS resourceType, s.create_time AS createTime, s.update_time AS updateTime FROM t_sys_role r LEFT JOIN t_sys_role_resource e ON r.id = e.role_id LEFT JOIN t_sys_resource s ON e.resource_id = s.id WHERE r.id in
#{item}
AND s.resource_type = 0 ORDER BY seq

SELECT DISTINCT s.id AS id, s.NAME AS 'name', s.url AS url, s.description AS description, s.icon AS icon, s.pid AS pid, s.seq AS seq, s.STATUS AS 'status', s.open_mode AS openMode, s.opened as opened, s.resource_type AS resourceType, s.create_time AS createTime, s.update_time AS updateTime FROM t_sys_role r LEFT JOIN t_sys_role_resource e ON r.id = e.role_id LEFT JOIN t_sys_resource s ON e.resource_id = s.id WHERE r.id in
#{item}
AND s.resource_type = 0 AND s.pid=#{pid} AND r.status = 0 ORDER BY seq

SELECT t.id, t.name, t.seq, t.description, t. status, t.create_time AS createTime, t.update_time AS updateTime FROM t_sys_role t
t.id=#{id}
and t.name like '%${name}%'
